import{_ as t,c as i,o as a,ag as o}from"./chunks/framework.BRWBV1zN.js";const p=JSON.parse('{"title":"MOV","description":"","frontmatter":{},"headers":[],"relativePath":"computer/instructions/mov.md","filePath":"computer/instructions/mov.md","lastUpdated":1754335345000}'),r={name:"computer/instructions/mov.md"};function d(n,e,c,s,l,m){return a(),i("div",null,e[0]||(e[0]=[o('<h1 id="mov" tabindex="-1">MOV <a class="header-anchor" href="#mov" aria-label="Permalink to &quot;MOV&quot;">​</a></h1><p>Esta instrucción copia el operando fuente en el operando destino. El operando fuente no se modifica. Los <a href="./../cpu.html#flags"><em>flags</em></a> no se modifican.</p><h2 id="uso" tabindex="-1">Uso <a class="header-anchor" href="#uso" aria-label="Permalink to &quot;Uso&quot;">​</a></h2><div class="language-vonsim vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">vonsim</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">MOV</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> dest, fuente</span></span></code></pre></div><p>Las combinaciones válidas de <em>dest</em>, <em>fuente</em> son:</p><ul><li>Registro, registro</li><li>Registro, dirección de memoria</li><li>Registro, inmediato</li><li>Dirección de memoria, registro</li><li>Dirección de memoria, inmediato</li></ul><p>El operando <code>dirección de memoria</code> puede ser directo o indirecto, directo es la dirección misma de memoria e indirecto la dirección esta en el registro BL.</p><p>(Ver <a href="./../assembly.html#operandos">tipos de operandos</a>)</p><h2 id="codificacion" tabindex="-1">Codificación <a class="header-anchor" href="#codificacion" aria-label="Permalink to &quot;Codificación&quot;">​</a></h2><h3 id="carga-a-registro" tabindex="-1">Carga a registro <a class="header-anchor" href="#carga-a-registro" aria-label="Permalink to &quot;Carga a registro&quot;">​</a></h3><ul><li>REGISTRO a registro<br><code>0000_RxRy</code></li><li>Memoria (directo) a registro<br><code>0001_Rx00</code>, <em>dir</em></li><li>Memoria (indirecto <code>BL</code>) a registro<br><code>0001_Rx01</code></li><li>Inmediato a registro<br><code>0001_Rx10</code>, <em>dato</em></li></ul><h3 id="almacenar-en-memoria" tabindex="-1">Almacenar en memoria <a class="header-anchor" href="#almacenar-en-memoria" aria-label="Permalink to &quot;Almacenar en memoria&quot;">​</a></h3><ul><li>Registro a memoria (directo)<br><code>0010_00Ry</code>, <em>dir</em></li><li>Registro a memoria (indirecto <code>BL</code>)<br><code>0010_01Ry</code></li><li>Inmediato a memoria (directo)<br><code>0010_1100</code>, <em>dir</em>, <em>dato</em></li><li>Inmediato a memoria (indirecto <code>BL</code>)<br><code>0010_1101</code>, <em>dato</em></li></ul><p><code>Rx</code> o <code>Ry</code>: Índices de registros, número entre <code>0</code> y <code>3</code>, cada índice es de 2 bits.</p><table tabindex="0"><thead><tr><th style="text-align:center;">Registro</th><th style="text-align:center;">Binario</th><th style="text-align:center;">Decimal</th></tr></thead><tbody><tr><td style="text-align:center;">AL</td><td style="text-align:center;">00</td><td style="text-align:center;">0</td></tr><tr><td style="text-align:center;">BL</td><td style="text-align:center;">01</td><td style="text-align:center;">1</td></tr><tr><td style="text-align:center;">CL</td><td style="text-align:center;">10</td><td style="text-align:center;">2</td></tr><tr><td style="text-align:center;">DL</td><td style="text-align:center;">11</td><td style="text-align:center;">3</td></tr></tbody></table>',15)]))}const g=t(r,[["render",d]]);export{p as __pageData,g as default};
